import Vue from 'vue'
import Vuex from 'vuex'
import {db} from '../main'


Vue.use(Vuex)

export default new Vuex.Store({
	// actions: {
	// 	async fetchPosts(ctx) {
	// 		const res = await fetch(
	// 			"https://travel-b8c69.firebaseio.com/posts", {'mode': 'no-cors'}
	// 		)
	// 		const posts = await res.json()
	// 		ctx.commit('updatePosts', posts)
	// 	}
	// },
	mutations: {
		updatePosts(state, posts) {
			state.posts = posts
		}
	},
	state: {
		posts: []
	},
	firestore() {
		return {
			posts: db.collection('posts')
		}
	},
	getters: {
		allPosts(state) {
			return state.posts
		}
	}
})
